import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Row, Col } from 'react-bootstrap';
import ClipLoader from "react-spinners/ClipLoader";
import './App.css';

const App = () => {
  const [tableData, setTableData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`https://api.spacexdata.com/v4/launches/?limit=10`);
        const data = response.data;
        setTableData(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const loadMoreData = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`https://api.spacexdata.com/v4/launches/?limit=10&offset=${tableData.length}`);
      const data = response.data;
      setTableData(prevData => [...prevData, ...data]);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching more data:', error);
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <div className="center-box">
        {tableData.map((launch, index) => (
          <Row className="tableData-descriptions" key={launch.id} style={{ flexWrap: 'nowrap' }}>
            <Col id="image-content" lg={6}>
              <img
                src={launch.links.patch.small}
                alt="Large Patch"
                className="img-fluid"
                loading="lazy"
              />
            </Col>
            <Col lg={6}>
              <p id="tableData-Header">{launch.flight_number}: {launch.name} ({launch.date_local.slice(0, 4)})</p>
              <p id="tableData-details">Details: {launch.details}</p>
            </Col>
          </Row>
        ))}
        {loading && <ClipLoader />}
        {!loading && tableData.length >= 10 && (
          <div style={{ textAlign: 'center', padding: '20px' }}>
            <button onClick={loadMoreData}>Load More</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
